#!/bin/bash
# ARK v3.1.0 | Module: MOTD Generator
# Classification: INTERNAL
# Purpose: Generate dynamic Message of the Day for SSH login

ARK_DIR="/opt/ark"
MOTD_FILE="/etc/motd"

# Get system information
VERSION=$(cat "$ARK_DIR/VERSION" 2>/dev/null || echo "3.1.1")
UPTIME=$(uptime -p 2>/dev/null | sed 's/up //' || echo "Unknown")
HOSTNAME=$(hostname)
TIMESTAMP=$(date '+%Y-%m-%d %H:%M:%S')

# Get ARK status
if command -v docker >/dev/null 2>&1; then
    RUNNING_CONTAINERS=$(docker ps --format "{{.Names}}" 2>/dev/null | wc -l)
    TOTAL_CONTAINERS=$(docker ps -a --format "{{.Names}}" 2>/dev/null | wc -l)
else
    RUNNING_CONTAINERS=0
    TOTAL_CONTAINERS=0
fi

# Determine power source (if available)
if [ -f /sys/class/power_supply/BAT0/capacity ] 2>/dev/null; then
    POWER_SOURCE="BATTERY ($(cat /sys/class/power_supply/BAT0/capacity)% capacity)"
elif [ -d /sys/class/power_supply/AC ] 2>/dev/null; then
    POWER_SOURCE="AC/GRID"
else
    POWER_SOURCE="UNKNOWN"
fi

# Generate MOTD
cat > "$MOTD_FILE" << EOF

${CYAN}╔═══════════════════════════════════════════════════════════════╗${NC}
${CYAN}║                                                               ║${NC}
${CYAN}║     █████╗ ██████╗ ██╗  ██╗                                 ║${NC}
${CYAN}║    ██╔══██╗██╔══██╗██║ ██╔╝                                 ║${NC}
${CYAN}║    ███████║██████╔╝█████╔╝                                  ║${NC}
${CYAN}║    ██╔══██║██╔══██╗██╔═██╗                                  ║${NC}
${CYAN}║    ██║  ██║██║  ██║██║  ██╗                                 ║${NC}
${CYAN}║    ╚═╝  ╚═╝╚═╝  ╚═╝╚═╝  ╚═╝                                 ║${NC}
${CYAN}║                                                               ║${NC}
${CYAN}║         AUTONOMOUS RESILIENT KERNEL v${VERSION}                    ║${NC}
${CYAN}║                                                               ║${NC}
${CYAN}╚═══════════════════════════════════════════════════════════════╝${NC}

${GREEN}OPERATIONAL STATUS:${NC}  NOMINAL
${GREEN}POWER SOURCE:${NC}        ${POWER_SOURCE}
${GREEN}SECURITY LEVEL:${NC}      AIR-GAPPED
${GREEN}UPTIME:${NC}              ${UPTIME}
${GREEN}CONTAINERS:${NC}          ${RUNNING_CONTAINERS}/${TOTAL_CONTAINERS} Running
